import React, { useState } from "react";
import "./App.css";
import { IQuestion } from "./models/IQuestion";
import Question from "./components/Question";


const TOTAL_QUESTIONS = 5;

export type AnswerObject = {
  question: string;
  answer: string;
  correct: boolean;
  correctAnswer: string;
};

function App() {
  const [loading, setLoading] = useState(false);
  const [questions, setQuestions] = useState<Array<IQuestion>>([]);
  const [userAnswers, setUserAnswers] = useState<AnswerObject[]>([]);
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(true);
  const [questionNumber, setquestionNumber] = useState(0);


  const start = async () => {
    setGameOver(false);

    //may you add useEffect
    setLoading(true);
    await fetch("questions.json")
      .then((response) => response.json())
      .then((Questions) => {
        setQuestions(Questions);
      });

    setLoading(false);
    setScore(0);
    setUserAnswers([]);
    setquestionNumber(0);
  };

  const nextQuestion = () => {
    const nextQuestion = questionNumber + 1;
    if (nextQuestion === TOTAL_QUESTIONS) {
      userAnswers.forEach(element => {
        if(element.answer === element.correctAnswer)
          setScore(score + 1);
      });
      setGameOver(true);
    } else {
      setquestionNumber(nextQuestion);
    }
  };

  const checkAnswer = (e: React.MouseEvent<HTMLButtonElement>) => {
    if (!gameOver) {
      const answer = e.currentTarget.value;
      const correct = questions[questionNumber].correct === answer;
      const answerObject = {
        question: questions[questionNumber].question,
        answer,
        correct,
        correctAnswer: questions[questionNumber].correct,
      };
      const answers= [...userAnswers];
      answers[questionNumber] = answerObject;
      setUserAnswers(answers);
    }
  };

  const submitAnswer = (e: React.MouseEvent<HTMLButtonElement>) => {
    const correct = questions[questionNumber].correct === userAnswers[questionNumber].answer;
    if (correct) setScore(score + 1);
  };

  return (
    <div className="App">
      <div className="trivia">
        <h1>Welcome to Trivia</h1>

        {gameOver || userAnswers.length === TOTAL_QUESTIONS ? (
          <>
          <button className="start" onClick={start}>
            Start Game
          </button>
          <h3 className="score">Score: {score} </h3>
          </>
        ) : null}



        {loading && <p>Loading Questions...</p>}

        {!loading && !gameOver && (

          <Question
            questionNum={questionNumber + 1}
            totalQuestions={TOTAL_QUESTIONS}
            question={questions[questionNumber].question}
            callback={checkAnswer}
            answer_1={questions[questionNumber].answer1}
            answer_2={questions[questionNumber].answer2}
            answer_3={questions[questionNumber].answer3}
            answer_4={questions[questionNumber].answer4}
            userAnswer={userAnswers ? userAnswers[questionNumber] : undefined}
            correctAns={questions[questionNumber].correct}
          />
        )}


        {!gameOver &&
        !loading &&
        userAnswers.length === questionNumber + 1 &&
        questionNumber !== TOTAL_QUESTIONS - 1 ? (
          <>
          <button className="next" onClick={nextQuestion}>
            Next Question
          </button>
          <button className="submit" onClick={submitAnswer}>
            Submit
          </button>
          </>
          ): null}

      </div>
    </div>
  );
}

export default App;
