import React from "react";
import { AnswerObject } from "../models/AnswerObject";
import './Question.css';

type Props = {
  question: string;
  answers: string[];
  correctAns: number;
  questionNum: number;
  totalQuestions: number;
  userAnswer: AnswerObject | undefined;
  callback: (e: React.MouseEvent<HTMLButtonElement>) => void;
};

let i=0;

const Question: React.FC<Props> = ({
  question,
  questionNum,
  totalQuestions,
  correctAns,
  answers,
  userAnswer,
  callback,
}) => (
  <div className='question'>
    <p className="number">
      Question: {questionNum} / {totalQuestions}
    </p>
    <p>{question}</p>
    <div>
    {
    answers.map((answer) => (
      <button className="answer" value={i++} onClick={callback}>{answer}</button>
    ))}
    </div>
  </div>
);

export default Question;
